import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Employee } from '../employee';
import { EmployeeService } from '../services/employee.service';
import { Modal } from 'bootstrap';

@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.scss'],
})
export class EmployeeListComponent implements OnInit {
  employees: Employee[] = [];
  formModal: Modal;

  constructor(
    private employeeService: EmployeeService,
    private router: Router
    ) {}

  ngOnInit(): void {
    this.getEmployees();


  }

  openModal() {
    this.formModal= new Modal(document.getElementById("deleteModal"));
    this.formModal.show();
  }

  doSomething() {
    this.formModal.hide();
  }


  private getEmployees() {
    this.employeeService.getEmployeesList().subscribe((data) => {
      this.employees = data;
    });
  }

  updateEmployee(id: number | undefined) {
    this.router.navigate(['update-employee', id]);
  }

  deleteEmployee(id: number | undefined) {
    this.employeeService.deleteEmployee(id).subscribe(data => {
      console.log(data);
      this.getEmployees();
    })
    this.formModal.hide()
  }

  employeeDetail(id: number | undefined) {
  this.router.navigate(['employee-detail', id])
  }
}
<h2 class="text-center">Employee List</h2>
<table class="table table-striped">
  <thead>
    <tr>
      <th>First Name</th>
      <th>Last Name</th>
      <th>Email Id</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let employee of employees">
      <td>{{ employee.firstName }}</td>
      <td>{{ employee.lastName }}</td>
      <td>{{ employee.emailId }}</td>
      <td>
        <button (click)="updateEmployee(employee.id)" class="btn btn-info">
          Update
        </button>
        <button
          class="btn btn-danger"
          type="button"
          data-bs-toggle="modal"
          data-bs-target="#deleteModal"
          (click)="openModal()"
        >
          Delete
        </button>

        <!-- Modal -->
        <div
          class="modal fade"
          id="deleteModal"
          tabindex="-1"
          aria-labelledby="deleteModalLabel"
          aria-hidden="true"
        >
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h1 class="modal-title fs-5" id="deleteModalLabel">
                  Delete this employee
                </h1>
                <button
                  type="button"
                  class="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                ></button>
              </div>
              <div class="modal-body">Are you sure?</div>
              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-secondary"
                  data-bs-dismiss="modal"
                >
                  Close
                </button>

                <button
                  type="button"
                  class="btn btn-danger"
                  (click)="deleteEmployee(employee.id)"
                  data-bs-dismiss="modal"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>

        <button (click)="employeeDetail(employee.id)" class="btn btn-danger">
          Detail
        </button>
      </td>
    </tr>
  </tbody>
</table>
