import { Component } from '@angular/core';
import { Employee } from '../employee';
import { EmployeeService } from '../services/employee.service'

@Component({
  selector: 'app-create-employee',
  templateUrl: './create-employee.component.html',
  styleUrls: ['./create-employee.component.scss']
})
export class CreateEmployeeComponent {
  constructor(private employeeService: EmployeeService) {}


  employees: Employee[] = [];

  onSubmit() {
    const newEmployee : Employee = new Employee();
      this.employeeService.addEmployee(newEmployee).subscribe(data => {
        console.log("Employee", data);
        this.employees.push(data);

      })
    }

  }
<h3>Create Employee</h3>
<form (ngSubmit) = "onSubmit()" *ngFor="let employee of employees">
    <div class="form-group">
        <label >First Name</label>
        <input type="text" class="form-control" id="firstName"
            [(ngModel)]="employee.firstName" name="firstName">
    </div>

    <div class="form-group">
        <label >Last Name</label>
        <input type="text" class="form-control" id="lastName"
            [(ngModel)]="employee.lastName" name="lastName">
    </div>

    <div class="form-group">
        <label >Email</label>
        <input type="email" class="form-control" id="emailId"
            [(ngModel)]="employee.emailId" name="emailId">
    </div>

    <button class="btn btn-success" type="submit">Create</button>
</form>
